import React, { useEffect, useState } from 'react';
import { Card, CardContent, Typography, Grid, Icon } from '@mui/material';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';
import UpdateIcon from '@mui/icons-material/Update';
import LinkIcon from '@mui/icons-material/Link';

const style = {
  gridContainer: {
    flexGrow: 1,
    margin:'50px 10%'
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column' as 'column', // Specifying the type directly
    // justifyContent: 'center',
    alignItems: 'flex-start',
    // padding: '20px',
    // textAlign:'left'
  },
  icon: {
    marginBottom: '20px',
  },
  cardContent: {
    display: 'flex',
    flexDirection: 'column' as 'column', // Specifying the type directly
    alignItems: 'flex-start',
    // textAlign: 'center',
    height: '100%', // Ensure content fills the entire card height
  },
  imageDiv:{
    height:'120px'
  },
  title:{
    fontSize: '38px',
  lineHeight: '46px',
  marginBottom: '10px',
  marginTop: '10px',
  color:'#000000',
  fontWeight: '900',
  fontFamily: 'Lato,sans-serif'
  },
  popOutCardHover: {
    transform: 'translateY(-10px)', /* Adjust the translateY for the pop-out effect */
    boxShadow: '0px 8px 16px rgba(0, 0, 0, 0.2)', /* Shadow effect */
  },
  
  // Add more styles as needed
};
const base64SVG = "data:image/png;base64,"
const searchIcon = "data:image/png;base64,"
const chatGpt = "data:image/png;base64,"
const resumeBuilder = "data:image/png;base64,"

const initialFeatures = [{
  icon: base64SVG,
  title: 'Verified jobs',
  description: `We gather job opportunities straight from employers' websites, ensuring top-quality and precise listings without any repetitions or unwanted messages.`
}, {
  icon: searchIcon,
  title: 'Up to date listings',
  description: 'Search recommends jobs tailored to your preferences, making it easier for you to find the most fitting opportunities that match your skills and interests.'
}, {
  icon: resumeBuilder,
  title: 'Resume builder',
  description: 'Resume builder service helps you craft professional resumes effortlessly, allowing you to create polished and impactful resumes that highlight your skills and experience effectively.'
},
 {
  icon: chatGpt,
  title: 'Now integrated with your favourite ChatGPT',
  description: 'Our integration with ChatGPT enables personalized skill recommendations'
}]
export default function FeatureGrid() {
  
  const [features, setFeatures] = useState(initialFeatures);
  const [currentIndex, setCurrentIndex] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => {
      const nextIndex = (currentIndex + 1) % features.length;
      setFeatures((prevFeatures) => [
        ...prevFeatures.slice(nextIndex),
        ...prevFeatures.slice(0, nextIndex),
      ]);
      setCurrentIndex(nextIndex);
    }, 1500); // Change the interval duration (in milliseconds) as needed

    return () => clearInterval(interval);
  }, [currentIndex, features]);
  return (
 
    <Grid container spacing={2} style={style.gridContainer}>
          <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center',width:'100%' }}>
  <Typography variant="h2" component="h2" style={style.title}>
    {"All set to make your search easier?"}
  </Typography>
  
  <Typography style={{ textAlign: 'center' }}>
    {"Connecting you directly to millions of current job listings on company career sites, we simplify the process of accessing opportunities and getting hired."}
  </Typography>
</div>
      {features.map((feature, index) => (
        <Grid item xs={12} sm={6} md={3} key={index} style={{ transition: 'transform 2.3s ease' }}>
 <Card
    style={{
      ...style.card,
      ...(style.popOutCardHover), // Apply pop-out effect conditionally
    }}
  >
          <CardContent style={style.cardContent}>
            <div style={style.imageDiv}>
            <img src={feature.icon} alt="Custom Icon" style={style.icon} />
            </div>
         
            <Typography variant="h6" component="h2" style={{ fontWeight: 'bold', marginBottom: '8px', textAlign:'left' }}>
  {feature.title}
</Typography>

            <Typography>
              {feature.description}
            </Typography>
            </CardContent>
          </Card>
         
        </Grid>
      ))}
    </Grid>
  );
}
